SELECT ticker, first(low, time), last(low, time), last(low, time) - first(low, time) as differential, (last(low, time) - first(low, time))*100/first(low, time) as pct
  FROM stock_prices WHERE $__timeFilter(time)
  GROUP BY ticker;

-- SELECT percentile_cont(0.5) WITHIN GROUP (ORDER BY ticker) FROM stock_prices;

SELECT ticker, sum(volume)
  FROM stock_prices WHERE $__timeFilter(time)
  GROUP BY ticker ORDER BY 2 desc;

